import React, { useState } from 'react'

export default function Connection({item, toggleshow}) {

  toggleshow()

  return (
    <div className='Connection-page'>
        <div className="connect">
            <img src={item.postedBy.photo} alt="" />
            <h1>{item.postedBy.name}</h1>
        </div>
        
    </div>
  )
}
